    val c = DeviceInfoServiceGrpc.newStub(GrpcService.instance.getMyChannel(force))
            val req = DeviceInfo.DeviceConfigRequest.newBuilder().setDeviceType("P")
                .setMac(DeviceUtils.getMacAddress()).build()
