)

func Connect(c *gin.Context) {
	fmt.Println("Connect Middleware")
	if db.Session == nil {
		fmt.Println("Error DB Session")
	}
	s := db.Session.Clone()

	defer s.Close()

	c.Set("db", s.DB(db.Mongo.Database))
	c.Next()  //这一句是必须的, 否则数据连接在跑完这个方法之后就被关闭了
}

func Middleware(c *gin.Context) {
