    // jao.Free;  这样会报异常   因为其实这里的jao仅仅是一个指针，而不是拷贝出来的对象
  end;
  //ja.Free;
  FreeAndNil(ja);

  //状况3 Values['Name'] 出来的对象， 其实这里和状况2类似
  jo := TJSONObject.ParseJSONValue('{"A": {"B":true}}') as TJSONObject;

  joo := jo.Values['A'] as TJSONObject;
