package main

import (
	"context"
	"crypto/sha256"
	"fmt"
	"github.com/linxlib/logs"
	"google.golang.org/grpc"
	"google.golang.org/grpc/credentials"
	"grpc_startup/proto"
	"log"
	"math"
	"net"
)

const (
	port = ":50051"
)

type server struct {
}

// verifyFile æ ¡éªŒä¸‹ä¸Šä¼ çš„æ•°æ®åŒ…æ˜¯å¦å®Œæ•´, é€šè¿‡Sha256å’Œæ–‡ä»¶æ•°æ®é•¿åº¦ä¸¤ä¸ªè¿›è¡Œåˆ¤æ–­
func (s *server) verifyFile(file []byte, hash string, length int64) bool {
	h := sha256.New()
	h.Write(file)
	myHash := fmt.Sprintf("%x", h.Sum(nil))
	logs.Info("hash:", hash, " myHash:", myHash, " len:", length, " myLen:", len(file))
	return hash == myHash
}

func (s *server) Upload(ctx context.Context, in *proto.FSReq) (*proto.FSResp, error) {
	if !s.verifyFile(in.File, in.Hash, in.Filelen) {
		return &proto.FSResp{
			Status:  false,
			Message: "æ•°æ®åŒ…å“ˆå¸Œæ ¡éªŒå¤±è´¥ï¼Œè¯·é‡æ–°éƒ¨ç½²",
		}, nil
	}
	return &proto.FSResp{
		Status:  true,
		Message: "received",
	}, nil
}

func main() {
	lis, err := net.Listen("tcp", port)
	if err != nil {
		logs.Fatalf("failed to listen: %v", err)
	}
	c, err := credentials.NewServerTLSFromFile("./server.crt", "./server.key")
	if err != nil {
		log.Fatalf("credentials.NewServerTLSFromFile err: %v", err)
	}
	//ç”±äºè¦å‘é€è¾ƒå¤§çš„å‹ç¼©åŒ…ï¼Œé»˜è®¤ä¸º 4Mã€‚
	//å¦‚æœéœ€è¦å‘å®¢æˆ·ç«¯å‘é€å¤§æ–‡ä»¶åˆ™å¢åŠ ä¸€æ¡grpc.MaxSendMsgSize()
	s := grpc.NewServer(
		grpc.Creds(c),
		grpc.MaxRecvMsgSize(math.MaxInt64))

	//æ³¨å†ŒæœåŠ¡
	proto.RegisterFileServiceServer(s, &server{})

	if err := s.Serve(lis); err != nil {
		log.Fatalf("failed to serve: %v", err)
	}
}
```

