	"google.golang.org/grpc"
	"google.golang.org/grpc/credentials"
	"grpc_startup/proto"
	"io/ioutil"
	"log"
	"time"
)

const (
	address = "localhost:50051"
)

func main() {
	// æ³¨æ„è¿™é‡Œçš„deployï¼Œ éœ€è¦å’Œè¯ä¹¦å…¬é’¥ç”Ÿæˆæ—¶çš„ Common Name å¯¹åº”
	c, err := credentials.NewClientTLSFromFile("./server.crt", "deploy")
	if err != nil {
